config.script:
        import com.workfusion.odf2.core.Odf
        import com.workfusion.assign.task.HelloWorldMailTask
        // after imports

        // before odf builder
        def odf = Odf.builder()
            .withInput(__taskInputMap.getWrappedObject())
            .withModules(new com.workfusion.odf2.core.webharvest.WebHarvestIntegrationModule(binding))
            
            // after modules
            .build()

        // after odf builder

        result = odf.runTask(HelloWorldMailTask)

        // after runTask

        rows = result.getRows()
        columns = result.getColumns()

        // after all
